import * as firebase from "firebase"

const firebaseConfig = {
  apiKey: "AIzaSyC70LAhPV1qHunyNoZbOAk7QRUEh57PPbQ",
  authDomain: "devter-6661a.firebaseapp.com",
  databaseURL: "https://devter-6661a.firebaseio.com",
  projectId: "devter-6661a",
  storageBucket: "devter-6661a.appspot.com",
  messagingSenderId: "807038871343",
  appId: "1:807038871343:web:cb90ded09f7def7d8063da",
  measurementId: "G-909SM8WT3B",
}


!firebase.apps.length && firebase.initializeApp(firebaseConfig)//verify 
const db = firebase.firestore();
   const mapUserFromFirebaseAuthToUser = (user) => {
  const { displayName, email, photoURL,uid } = user
  return {
    avatar: photoURL,
    username: displayName,
    email,
    uid
  }
}
export const onAuthStateChanged = (onChange) => {
  return firebase.auth().onAuthStateChanged((user) => {
    const normalizedUser = user ? mapUserFromFirebaseAuthToUser(user) : null
    onChange(normalizedUser)
  })
}

export const loginWithGitHub = () => {
  const githubProvider = new firebase.auth.GithubAuthProvider()
  return firebase.auth().signInWithPopup(githubProvider)
}


export const addDevid = ({avatar ,uid, message, username})=>{
  console.log(avatar, uid, message, username)
  return db.collection('devits').doc().set(
    {avatar,
      uid,
      message,
      username,
      createdAt: firebase.firestore.Timestamp.fromDate(new Date()),
      likeCount:0,
      sharedCount:0
    }).then(res=>console.log("documento saved")).catch(err=>console.log(err))
}
